mapper.select:
SELECT USER_ID
FROM users
WHERE USER_NAME = #{userName} AND USER_BIRTHDAY = #{userBirth}

SELECT ACCOUNT_ID
FROM users
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM users
WHERE USER_ID = #{userId} AND USER_BIRTHDAY = #{userBirth}

mapper.update:
UPDATE users
SET USER_PW = #{changePw}
WHERE ACCOUNT_ID = #{accountId}
